mapper.select:

select 

from t_class_course
where f_class_group_id = #{fClassGroupId,jdbcType=INTEGER}
  and f_course_id = #{fCourseId,jdbcType=INTEGER}
  and f_class_id = #{fClassId,jdbcType=INTEGER}

mapper.delete:

delete from t_class_course
where f_class_group_id = #{fClassGroupId,jdbcType=INTEGER}
  and f_course_id = #{fCourseId,jdbcType=INTEGER}
  and f_class_id = #{fClassId,jdbcType=INTEGER}

mapper.update:

update t_class_course


    f_course_count = #{fCourseCount,jdbcType=INTEGER},


    f_have_course_count = #{fHaveCourseCount,jdbcType=INTEGER},


where f_class_group_id = #{fClassGroupId,jdbcType=INTEGER}
  and f_course_id = #{fCourseId,jdbcType=INTEGER}
  and f_class_id = #{fClassId,jdbcType=INTEGER}


update t_class_course
set f_course_count = #{fCourseCount,jdbcType=INTEGER},
  f_have_course_count = #{fHaveCourseCount,jdbcType=INTEGER}
where f_class_group_id = #{fClassGroupId,jdbcType=INTEGER}
  and f_course_id = #{fCourseId,jdbcType=INTEGER}
  and f_class_id = #{fClassId,jdbcType=INTEGER}
