mapper.select:

select 

from temp_school_time_table
where f_week_id = #{fWeekId,jdbcType=INTEGER}
  and f_count_id = #{fCountId,jdbcType=INTEGER}
  and f_teacher_id = #{fTeacherId,jdbcType=INTEGER}
  and f_class_id = #{fClassId,jdbcType=INTEGER}
  and f_course_id = #{fCourseId,jdbcType=INTEGER}

mapper.delete:

delete from temp_school_time_table
where f_week_id = #{fWeekId,jdbcType=INTEGER}
  and f_count_id = #{fCountId,jdbcType=INTEGER}
  and f_teacher_id = #{fTeacherId,jdbcType=INTEGER}
  and f_class_id = #{fClassId,jdbcType=INTEGER}
  and f_course_id = #{fCourseId,jdbcType=INTEGER}

mapper.update:

update temp_school_time_table


    f_course_num = #{fCourseNum,jdbcType=INTEGER},


    f_conefict_flag = #{fConefictFlag,jdbcType=INTEGER},


    f_type_conefict_id = #{fTypeConefictId,jdbcType=INTEGER},


    f_class_conefict_id = #{fClassConefictId,jdbcType=INTEGER},


    f_course_type = #{fCourseType,jdbcType=INTEGER},


where f_week_id = #{fWeekId,jdbcType=INTEGER}
  and f_count_id = #{fCountId,jdbcType=INTEGER}
  and f_teacher_id = #{fTeacherId,jdbcType=INTEGER}
  and f_class_id = #{fClassId,jdbcType=INTEGER}
  and f_course_id = #{fCourseId,jdbcType=INTEGER}


update temp_school_time_table
set f_course_num = #{fCourseNum,jdbcType=INTEGER},
  f_conefict_flag = #{fConefictFlag,jdbcType=INTEGER},
  f_type_conefict_id = #{fTypeConefictId,jdbcType=INTEGER},
  f_class_conefict_id = #{fClassConefictId,jdbcType=INTEGER},
  f_course_type = #{fCourseType,jdbcType=INTEGER}
where f_week_id = #{fWeekId,jdbcType=INTEGER}
  and f_count_id = #{fCountId,jdbcType=INTEGER}
  and f_teacher_id = #{fTeacherId,jdbcType=INTEGER}
  and f_class_id = #{fClassId,jdbcType=INTEGER}
  and f_course_id = #{fCourseId,jdbcType=INTEGER}
